interface IAs {
    function mint(address to, uint256 amount) external;

    function burn(address from, uint256 amount) external;
}","keccak256":"0xc648a9e850a211fee697ec20aacddd24dbc9cc07682db9c88f9ef67c10216663","license":"MIT"}},"version":1}
